addEventListener('fetch', event => {
  event.respondWith(handleRequest(event.request));
})
const faviconIco = "data:image/png;base64,"
const iconImg = "data:image/png;base64,"
async function handleRequest(request) {
  const htmlContent = `
<!DOCTYPE html>
<html lang="fa" dir="rtl">
<head>
    <meta charset="UTF-8">
    <link rel="icon" href="${faviconIco}" type="image/x-icon" />
    <title>اطلاعات آب و هوا</title>
    <style>
        body { direction: rtl; }
        table { width: 100%; border-collapse: collapse; }
    th, td { 
        border: 1px solid #ddd; 
        padding: 8px; 
        text-align: center; /* Center-align content within table headers and cells */
    }
    th { background-color: #f2f2f2; }
    .temp { 
        direction: ltr;}
    </style>
    <script src="https://cdn.jsdelivr.net/npm/chart.js"></script>
</head>
<body>

<div class="right-text">
    <a href="https://sabaat.ir"><img src="${iconImg}" /></a>
    <p>Sabaat</p>
</div>
    <h2>یک شهر را انتخاب کنید:</h2>
    <select id="citySelect" onchange="fetchWeatherData()">
    <option value="">لطفا یک شهر را انتخاب کنید</option>
    <option value="35.6892,51.3890">تهران</option>
    <option value="34.3516,61.8814">مشهد</option>
    <option value="32.6554,51.6667">اصفهان</option>
    <option value="29.6175,52.5314">شیراز</option>
    <option value="38.0892,46.3007">تبریز</option>
    <option value="35.8028,50.9028">کرج</option>
    <option value="31.5447,48.6677">اهواز</option>
    <option value="34.6419,50.8803">قم</option>
    <option value="34.8056,47.0681">کرمانشاه</option>
    <option value="37.5667,45.7333">ارومیه</option>
    </select>

    <h3>اطلاعات آب و هوا - 30 روز گذشته + 7 روز آینده</h3>
    <div id="weatherTable"></div>
    <div>
    
    <canvas id="tempChart" width="400" height="200"></canvas>
    </div>
    <script>
    function getDayOfWeekInPersian(dateString) {
        const daysOfWeekInPersian = ['یکشنبه', 'دوشنبه', 'سه‌شنبه', 'چهارشنبه', 'پنج‌شنبه', 'جمعه', 'شنبه'];
        const date = new Date(dateString);
        const dayOfWeek = date.getDay();
        return daysOfWeekInPersian[dayOfWeek];
    }
    function gregorian_to_jalali(gy, gm, gd){
        var g_d_m = [0, 31, 59, 90, 120, 151, 181, 212, 243, 273, 304, 334];
        var jy, jm, jd;
        if (gy > 1600) {
            jy = 979;
            gy -= 1600;
        } else {
            jy = 0;
            gy -= 621;
        }
        var gy2 = (gm > 2) ? (gy + 1) : gy;
        var days = (365 * gy) + (parseInt((gy2 + 3) / 4)) - (parseInt((gy2 + 99) / 100)) + (parseInt((gy2 + 399) / 400)) - 80 + gd + g_d_m[gm - 1];
        jy += 33 * (parseInt(days / 12053));
        days %= 12053;
        jy += 4 * (parseInt(days / 1461));
        days %= 1461;
        if (days > 365) {
            jy += parseInt((days - 1) / 365);
            days = (days - 1) % 365;
        }
        jm = (days < 186) ? 1 + parseInt(days / 31) : 7 + parseInt((days - 186) / 30);
        jd = 1 + ((days < 186) ? (days % 31) : ((days - 186) % 30));
        return [jy, jm, jd].map(function(n){ return n < 10 ? '0' + n : '' + n; }).join('/');
    }

        async function fetchWeatherData() {
            const cityCoords = document.getElementById('citySelect').value;
            if(!cityCoords) return;
            
            const [latitude, longitude] = cityCoords.split(',');
            const apiURL = \`https://api.open-meteo.com/v1/forecast?latitude=\${latitude}&longitude=\${longitude}&daily=temperature_2m_max,temperature_2m_min,precipitation_sum&timezone=auto&past_days=30\`;

            const response = await fetch(apiURL);
            const data = await response.json();
            
            document.getElementById('weatherTable').innerHTML = generateHTMLTable(data);
        }
        function generateHTMLTable(data) {
            if (!data || !data.daily) return '<p>No data available.</p>';
            
            let tableRows = data.daily.time.map((time, index) => {
                const [gy, gm, gd] = time.split('-').map(Number);
                const jalaliDate = gregorian_to_jalali(gy, gm, gd);
                const dayOfWeek = getDayOfWeekInPersian(time);
                const condition = getWeatherCondition(data.daily.precipitation_sum[index], data.daily.temperature_2m_max[index]);
                const conditionImage = getConditionImage(condition);
                return \`<tr>
                            <td>\${dayOfWeek} \${jalaliDate} </td>
                            <td class="temp">\${time}</td>
                            <td class="temp">\${data.daily.temperature_2m_max[index]}°C</td>
                            <td class="temp">\${data.daily.temperature_2m_min[index]}°C</td>
                            <td><img src="\${conditionImage}" alt="\${condition}" style="width: 30px; height: 30px;"></td>
                          </tr>\`;
            }).join('');

            return \`
            <table>
                <thead>
                    <tr>
                        <th>تاریخ شمسی </th>
                        <th>تاریخ میلادی </th>
                        <th>حداکثر دما</th>
                        <th>حداقل دما</th>
                        <th>وضعیت</th>
                    </tr>
                </thead>
                <tbody>
                    \${tableRows}
                </tbody>
            </table>
            \`;
            const labels = data.daily.time.map(time => {
                const [gy, gm, gd] = time.split('-').map(Number);
                return gregorian_to_jalali(gy, gm, gd); // Use Jalali dates as labels
            });
            const maxTemps = data.daily.temperature_2m_max;
            const minTemps = data.daily.temperature_2m_min;
        
            // Render the chart
            const ctx = document.getElementById('tempChart').getContext('2d');
            const tempChart = new Chart(ctx, {
                type: 'line',
                data: {
                    labels: labels,
                    datasets: [{
                        label: 'Max Temperature (°C)',
                        data: maxTemps,
                        borderColor: 'rgb(255, 99, 132)',
                        backgroundColor: 'rgba(255, 99, 132, 0.2)',
                    }, {
                        label: 'Min Temperature (°C)',
                        data: minTemps,
                        borderColor: 'rgb(54, 162, 235)',
                        backgroundColor: 'rgba(54, 162, 235, 0.2)',
                    }]
                },
                options: {
                    scales: {
                        y: {
                            beginAtZero: false
                        }
                    }
                }
            });
        }

        function getConditionImage(condition) {
            
            const sunnyBase64 = "data:image/jpeg;base64,";
            const rainyBase64 = "data:image/jpeg;base64,";
            const cloudyBase64 = "data:image/jpeg;base64,";
            switch (condition) {
                case 'Sunny':
                    return sunnyBase64;
                case 'Rainy':
                    return rainyBase64;
                case 'Cloudy':
                    return cloudyBase64;
                default:
                    return ''; // Default or empty if unknown
            }
        }

        function getWeatherCondition(precipitation, maxTemp) {
            // Simplified logic to determine weather condition
            if (precipitation > 0) return 'Rainy';
            if (maxTemp > 25) return 'Sunny';
            return 'Cloudy';
        }
       
      

    </script>


</body>
</html>
  `;

  return new Response(htmlContent, {
    headers: { 'Content-Type': 'text/html;charset=UTF-8' },
  });
}
